select

from dis_member_info
where is_delete='N'

  and  id = #{id,jdbcType=INTEGER}


  and dis_user_id = #{disUserId,jdbcType=VARCHAR}


  and  dis_model_id=#{disModelId,jdbcType=VARCHAR}


  and  dis_model_id=#{disModelId,jdbcType=VARCHAR}


  and  dis_platform_id=#{disPlatformId,jdbcType=VARCHAR}



    limit pageStart,pageSize

select
count(*)
from dis_member_info
where is_delete='N'

  and  id = #{id,jdbcType=INTEGER}


  and dis_user_id = #{disUserId,jdbcType=VARCHAR}


  and  dis_model_id=#{disModelId,jdbcType=VARCHAR}

SELECT m.`id`,m.`dis_platform_id`,m.`dis_user_id`,m.`dis_model_id`,m.`dis_full_index`,m.`dis_user_name`,m.`dis_level`,m.`update_time`
,m.`dis_note`,m.`add_time`,d.`dis_value` AS dis_user_type
FROM  `dis_member_info` m
LEFT JOIN `dis_dictionary` d ON m.`dis_user_type`=d.`dis_type` AND d.`dis_code`='disUserType' AND d.`is_delete`='N'
where m.is_delete='N'

  and  m.id = #{id,jdbcType=INTEGER}


  and m.dis_user_id = #{disUserId,jdbcType=VARCHAR}


  and  m.dis_model_id=#{disModelId,jdbcType=VARCHAR}



    limit #{pageStart},#{pageSize}

